draw((0,0)--(2.5,1)--(5,0),Arrow);
draw((0,0)--(2.5,-1)--(5,0),Arrow);
draw(B--(7.5,1)--C,Arrow);
draw(B--(7.5,-1)--C,Arrow);
draw(A--(5,3)--C,Arrow);
[/asy]

